import dotenv from 'dotenv';
dotenv.config();
import express, { Application } from 'express';
import cors from 'cors';
import helmet from 'helmet';
import routes from './routes';
import ErrorHandler from './middlewares/error.middleware';
import Logger from './config/logger';
import morgan from 'morgan';
import swaggerUi from 'swagger-ui-express';
import swaggerJsdoc from 'swagger-jsdoc';
const swagger = require ("./swagger/swagger.json");

class App {
  public app: Application;
  public host: string | number;
  public port: string | number;
  public api_version: string | number;
  public env: boolean;
  private logStream = Logger.logStream;
  private logger = Logger.logger;
  public errorHandler = new ErrorHandler();

  constructor() {
    this.app = express();
    this.host = process.env.APP_HOST;
    this.port = process.env.APP_PORT;
    this.api_version = process.env.API_VERSION;

    this.initializeMiddleWares();
    this.initializeRoutes();
    this.initializeErrorHandlers();
    this.startApp();
  }

  public initializeMiddleWares(): void {
    this.app.use(cors());
    this.app.use(helmet());
    this.app.use(express.urlencoded({ extended: true }));
    this.app.use(express.json());
    this.app.use(morgan('combined', { stream: this.logStream }));
  }

  public initializeRoutes(): void {
    this.app.use(
      //`/api/${this.api_version}`,
      routes()
    );
    this.app.use('/api-docs', swaggerUi.serve, swaggerUi.setup(swagger));
  }

  public initializeErrorHandlers(): void {
    this.app.use(this.errorHandler.appErrorHandler);
    this.app.use(this.errorHandler.genericErrorHandler);
    this.app.use(this.errorHandler.notFound);
  }

  public startApp(): void {
    this.app.listen(this.port, () => {
      this.logger.info(
        // `Server started at ${this.host}:${this.port}/api/${this.api_version}/`
        `Server started at ${this.host}:${this.port}`
      );
    });
  }

  public getApp(): Application {
    return this.app;
  }
}

const app = new App();

export default app;

import express, { IRouter } from 'express';
const router = express.Router();
import userRoute from './user.route';
import notesRoute from './notes.route';

/**
 * Function contains Application routes
 *
 * @returns router
 */

const routes = (): IRouter => {
  router.get('/', (req, res) => {
    res.json('Welcome to fundoo Notes App');
  });
  router.use('/users', new userRoute().getRoutes());
  router.use('/notes', new notesRoute().getRoutes());

  return router;
};

export default routes;

import express, { IRouter } from 'express';
import userController from '../controllers/user.controller';
import userValidator from '../validators/user.validator';
import { userAuth, forgetUserAuth } from '../middlewares/auth.middleware';

class UserRoutes {
  private UserController = new userController();
  private router = express.Router();
  private UserValidator = new userValidator();

  constructor() {
    this.routes();
  }

  private routes = () => {

    // register the new user
    this.router.post('/register', this.UserValidator.newUser, this.UserController.registerUser);

    // Login user
    this.router.post('/login', this.UserValidator.login, this.UserController.loginUser);

    //route to Get a user by their id
    this.router.get('/getuser', userAuth, this.UserController.getUser);

    //route to update a user
    this.router.put('/updateuser', userAuth, this.UserController.updateUser);

    //route to delete a user by their id
    this.router.delete('/deleteuser',userAuth,  this.UserController.deleteUser);

    //route to Get all user 
    this.router.get('/', this.UserController.getAllUsers);

    // route to Change password
    this.router.post('/change',userAuth, this.UserController.change);

    // route to forget password
    this.router.post('/forget',this.UserController.forget);

    // route to reset password
    this.router.post('/reset',forgetUserAuth,this.UserController.reset);
  };

  public getRoutes = (): IRouter => {
    return this.router;
  };
}

export default UserRoutes;

