import HttpStatus from 'http-status-codes';
import notesService from '../services/notes.service';

import { Request, Response, NextFunction } from 'express';

class NotesController {
  public NotesService = new notesService();

  //create the new Notes
  public createNote = async (
    req: Request,
    res: Response,
    next: NextFunction
  ): Promise<any> => {
    try {
      const data = await this.NotesService.createNote(
        (req as any).id,
        req.body
      );
      res.status(HttpStatus.CREATED).json({
        code: HttpStatus.CREATED,
        data: data,
        message: 'Notes Created successfully'
      });
    } catch (error) {
      next(error);
    }
  };

  // Get All Notes
  public getAllNotes = async (
    req: Request,
    res: Response,
    next: NextFunction
  ): Promise<any> => {
    try {
      const data = await this.NotesService.getAllNotes((req as any).id);
      res.status(HttpStatus.OK).json({
        code: HttpStatus.OK,
        data: data,
        message: 'All Notes fetched successfully'
      });
    } catch (error) {
      next(error);
    }
  };

  //Get Specific Note
  public getSpecificNotes = async (
    req: Request,
    res: Response,
    next: NextFunction
  ): Promise<any> => {
    try {
      const data = await this.NotesService.getSpecificNotes(
        req.params.id,
        (req as any).id
      );
      res.status(HttpStatus.OK).json({
        code: HttpStatus.OK,
        data: data,
        message: 'Note fetched successfully'
      });
    } catch (error) {
      next(error);
    }
  };

  // Update Note
  public updateNotes = async (
    req: Request,
    res: Response,
    next: NextFunction
  ): Promise<any> => {
    try {
      const data = await this.NotesService.updateNotes(
        req.params.id,
        (req as any).id,
        req.body
      );
